import { CREATE_PERSON, EDIT_HOLDING} from '../actions/types';

const INTIAL_STATE = {
    idperson: null,
    holdings: 0
};

export default (state = INTIAL_STATE, action) => {
  switch (action.type) {
    case CREATE_PERSON:
      return { ...state, [action.payload]: action.payload};
    case EDIT_HOLDING:
      return { ...state, holdings: false, userId: null };
    default:
      return state;
  }
};
