import React from "react";
import { connect } from 'react-redux';
import { getStocks } from '../actions';
import * as ReactBootStrap from "react-bootstrap";

class SearchBar extends React.Component {
  state = { company: "" }; 
  onInputChange = (event) => {
    this.setState({ company: event.target.value.toUpperCase() });
  };
  onFormSubmit = (event) =>{ //this is a callback prop function to parent
      event.preventDefault();
      this.props.getStocks(this.state.company);


  }
  render() {
    return (
      <div>
        <div className="search-bar ui segment">
        <form onSubmit = {this.onFormSubmit}className="ui form">
          <div className="field">
            <label>Search by Ticker Symbol</label>
            <input
              type="text"
              value={this.state.company}
              onChange={this.onInputChange}
            />
          </div>
        </form>
      </div>
      <ReactBootStrap.Table striped bordered hover>
      <thead>
        <tr>
          <th>open</th>
          <th>high</th>
          <th>low</th>
          <th>close</th>
          <th>adjusted close</th>
          <th>volume</th>
          <th>dividend amount</th>
          <th>split coefficient</th>
        </tr>
        <tbody>

        </tbody>

      </thead>
    </ReactBootStrap.Table>
      </div>

    );
  }
}
const mapStateToProps = (state) => {
  return {
    streams: Object.values(state.streams),
    currentUserId: state.auth.userId,
    isSignedIn: state.auth.isSignedIn,
  };
};

export default connect(
  mapStateToProps,
  { getStocks }
)(SearchBar);
