import React, { Component } from "react";
import Form from "./StockForm";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

class StockPopup extends Component {
  state = {
    fields: {},
    buyDate: "",
    sellDate: ""
  };

  onChange = updatedValue => {

    this.setState({
      fields: {
        ...this.state.fields,
        ...updatedValue
      }
    });
    //make api call 
  };
  // dateParse = (date) => {
  //   var onlyDate = date.toString().substr(0,date.toString().indexOf(','));
  //   console.log(onlyDate);
  //   return onlyDate
  // }
  handleChange = date => {  
   // var hinga = new Date(new date.getFullYear(),new date.getMonth() , new date.getDate());
   console.log(date);
    var hinga = `${date.getFullYear().toString()}-${date.getMonth().toString()}-${date.getDate().toString()}`;

    console.log(hinga);

    this.setState({
      buyDate: date
    });
  };

  render() {
    return (
      <div className="App">
        <Form onChange={fields => this.onChange(fields)} />
        <DatePicker selected={this.state.buyDate} onChange={this.handleChange}/>
        <p>{JSON.stringify(this.state, null, 3)}</p>

      </div>
    );
  }
}


export default StockPopup;

