import React, { Component } from "react";
import Form from "./StockForm";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

class StockPopup extends Component {
  state = {
    fields: {},
    buyDate: "",
    sellDate: ""
  };

  onChange = updatedValue => {
    this.setState({
      fields: {
        ...this.state.fields,
        ...updatedValue
      }
    });
    //make api call 
  };
  handleChange = date => {
    var visitDate = new Date (date.getUTCFullYear(), date.getUTCMonth(), date.getUTCDate());
    this.setState({
      buyDate: visitDate
    });
  };

  render() {
    return (
      <div className="App">
        <Form onChange={fields => this.onChange(fields)} />
        <DatePicker selected={this.state.buyDate} onChange={this.handleChange}/>
        <p>{JSON.stringify(this.state, null, 3)}</p>

      </div>
    );
  }
}

export default StockPopup;

