import React from "react";
import { ResponsiveContainer } from "recharts";
import Title from "./Title";
import Plot from "react-plotly.js";
import { connect } from "react-redux";
import { getStocks } from "../actions";

class Chart extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      stockChartXValues: [],
      stockChartYValues: [],
    };
  }

  componentDidMount() {
    setTimeout(function () {
        fetchStock();
  }, 1000);
  }
  componentDidUpdate(prevProps) {
    if (prevProps.currCompany !== this.props.currCompany) {
      this.fetchStock();
    }
  }

  fetchStock() {
    console.log(this.props.historics);
    console.log("historics reached")
    let stockChartXValuesFunction = [];
    let stockChartYValuesFunction = [];
    for (var key in this.props.historics) {
      stockChartXValuesFunction.push(key);
      stockChartYValuesFunction.push(this.props.historics[key]["1. open"]);
    }
    this.setState({
      stockChartXValues: stockChartXValuesFunction,
      stockChartYValues: stockChartYValuesFunction,
    });
    console.log(this.props.historics);
  }
  render() {
    return (
      <React.Fragment>
        <Title>Today</Title>
        <ResponsiveContainer>
          <Plot
            data={[
              {
                x: this.state.stockChartXValues,
                y: this.state.stockChartYValues,
                type: "scatter",
                mode: "lines+markers",
                marker: { color: "red" },
              },
            ]}
            layout={{ width: 850, height: 440, title: this.props.currCompany }}
          />
        </ResponsiveContainer>
      </React.Fragment>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    currCompany: state.stocks.currentCompany,
    historics: state.stocks.companyInfo,
  };
};
export default connect(mapStateToProps, { getStocks })(Chart);
