    //     }
    //   )
    //   .then(
    //     function(data) {
    //       console.log(data);

    //       for (var key in data['Time Series (Daily)']) {
    //         stockChartXValuesFunction.push(key);
    //         stockChartYValuesFunction.push(data['Time Series (Daily)'][key]['1. open']);
    //       }

    //       // console.log(stockChartXValuesFunction);
    //       pointerToThis.setState({
    //         stockChartXValues: stockChartXValuesFunction,
    //         stockChartYValues: stockChartYValuesFunction
    //       });
    //     }
    //   )

          console.log(this.props.currentCompany)
          for (var key in this.props.data){
            stockChartXValuesFunction.push(key);
            stockChartYValuesFunction.push(this.props.data['1. open']);
          }
          this.setState({stockChartXValues: stockChartXValuesFunction,stockChartYValues: stockChartYValuesFunction})
  }
  render() {
    return  (
      <React.Fragment>
      <Title>Today</Title>
      <ResponsiveContainer>
      <Plot
          data={[
            {
              x: this.state.stockChartXValues,
              y: this.state.stockChartYValues,
              type: 'scatter',
              mode: 'lines+markers',
              marker: {color: 'red'},
            }
          ]}
          layout={{width: 850, height: 440, title: 'A Fancy Plot'}}
        />
      </ResponsiveContainer>
    </React.Fragment>
    );
  };
}
const mapStateToProps = (state) => {
  return {
    companies:state.stocks.currentCompany,
    data:state.stocks.companyInfo
  };
};
export default connect(
  mapStateToProps,
  { getStocks }
)(Chart);
