import React from "react";
import Link from "@material-ui/core/Link";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Title from "./Title";
import StockPopup from "../components/streams/StockPopup";
import Popup from "reactjs-popup";
import "reactjs-popup/dist/index.css";
import { connect } from "react-redux";
import { fetchStocks } from "../actions";

// Generate Order Data in to object
function createData(id, date, name, shipTo, paymentMethod, amount) {
  return { id, date, name, shipTo, paymentMethod, amount };
}

const rows = [
  createData(
    0,
    "16 Mar, 2019",
    "Elvis Presley",
    "Tupelo, MS",
    "VISA ⠀•••• 3719",
    312.44
  ),
  createData(
    1,
    "16 Mar, 2019",
    "Paul McCartney",
    "London, UK",
    "VISA ⠀•••• 2574",
    866.99
  ),
  createData(
    2,
    "16 Mar, 2019",
    "Tom Scholz",
    "Boston, MA",
    "MC ⠀•••• 1253",
    100.81
  ),
  createData(
    3,
    "16 Mar, 2019",
    "Michael Jackson",
    "Gary, IN",
    "AMEX ⠀•••• 2000",
    654.39
  ),
  createData(
    4,
    "15 Mar, 2019",
    "Bruce Springsteen",
    "Long Branch, NJ",
    "VISA ⠀•••• 5919",
    212.79
  ),
];

class Orders extends React.Component {
  componentDidMount() {
    this.props.fetchStocks();
  }
  componentDidUpdate(prevProps) {
    if (prevProps.listOfStocks === this.props.listOfStocks) {
      this.props.fetchStocks();
    }
  }
  fetchem = () => {
    this.props.fetchStocks();
  };
  render() {
    console.log(this.props.listOfStocks);
    return (
      <React.Fragment>
        <Title>Summary</Title>
        <Table size="small">
          <TableHead>
            <TableRow>
              <TableCell></TableCell>
              <TableCell>Last Price(yeterdays price)</TableCell>
              <TableCell>todays-yesterdays price</TableCell>
              <TableCell>Today's Gain/Loss</TableCell>
              <TableCell></TableCell>
              <TableCell></TableCell>
              <TableCell></TableCell>
              <TableCell></TableCell>
              <TableCell>Cost Basis</TableCell>
              <TableCell></TableCell>
            </TableRow>
            <TableRow>
              <TableCell>Name</TableCell>
              <TableCell>Price</TableCell>
              <TableCell>Change</TableCell>
              <TableCell>Dollar</TableCell>
              <TableCell>Percent</TableCell>
              <TableCell>Dollar</TableCell>
              <TableCell>Current Value</TableCell>
              <TableCell>Quantity</TableCell>
              <TableCell>Per Share</TableCell>
              <TableCell align="right">Total</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {Object.keys(this.props.listOfStocks).map((keyName, i) => (
              <TableRow key={i}>
                <TableCell>{this.props.listOfStocks[keyName].symbol}</TableCell>
                <TableCell>
                  {this.props.listOfStocks[keyName].opentoday}
                </TableCell>
                <TableCell>
                  {(
                    this.props.listOfStocks[keyName].purchaseprice -
                    this.props.listOfStocks[keyName].opentoday
                  ).toFixed(2)}
                </TableCell>
                <TableCell>
                  {(
                    this.props.listOfStocks[keyName].purchaseprice -
                    this.props.listOfStocks[keyName].opentoday
                  ).toFixed(2) * this.props.listOfStocks[keyName].quantity}
                </TableCell>
                <TableCell>{((1-(this.props.listOfStocks[keyName].opentoday /
                    this.props.listOfStocks[keyName].purchaseprice))*100).toFixed(2)}</TableCell>
                <TableCell></TableCell>
                <TableCell>
                  {this.props.listOfStocks[keyName].currentprice}
                </TableCell>
                <TableCell>
                  {this.props.listOfStocks[keyName].quantity}
                </TableCell>
                <TableCell></TableCell>
              </TableRow>
            ))}
            {/* {rows.map((row) => (
              <TableRow key={row.id}>
                <TableCell>{row.date}</TableCell>
                <TableCell>{row.name}</TableCell>
                <TableCell>{row.shipTo}</TableCell>
                <TableCell>{row.paymentMethod}</TableCell>
                <TableCell align="right">{row.amount}</TableCell>
              </TableRow>
            ))} */}
          </TableBody>
        </Table>
        <div>
          <Popup trigger={<button>Buy Stocks</button>} position="right center">
            <StockPopup />
          </Popup>
          <button onClick={this.fetchem}>fetchem all</button>
        </div>
      </React.Fragment>
    );
  }
}
const mapStateToProps = (state) => {
  return {
    listOfStocks: state.stocks.listStocks,
  };
};
export default connect(mapStateToProps, { fetchStocks })(Orders);

// be able to edit holding price
