import React from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

function createData(symbol, datePurchased, purchasePrice, qty, ) {
  return { symbol, datePurchased, purchasePrice, qty };
}
export default class StockForm extends React.Component {
  state = {
    symbol: "",
    quantity: "",
    fields: {},
    buyDateString:"",
    price:0
  };

  change = e => {
    this.props.onChange({ [e.target.name]: e.target.value });
    this.setState({
      [e.target.name]: e.target.value
    });
    console.log(e.target.name);
  };
// resets data but i want additional functionality 
// here i want to take state data and put it into object
//pass that object into action creator and dispatch it to database
//then i want to pull the data from database and dispaly it on Orders
//refer to action creator of steram create it creates and objects and posts it to the api 

  onSubmit = e => {
    console.log(e);

    e.preventDefault();
    this.setState({
      symbol: "",
      quantity: "",

    });
    this.props.onChange({
      symbol: "",
      quantity: "",
    });
  };
  handleChange = date => {  
    var stringDate = `${date.getFullYear().toString()}-${(date.getMonth()+1).toString()}-${date.getDate().toString()}`;
    this.setState({
      buyDate: date,
      buyDateString:stringDate
    });
  };

  render() {
    return (
      <form>
        <label>What day did you buy stock?</label>
        <DatePicker selected={this.state.buyDate} onChange={this.handleChange}/>
        <input
          name="symbol"
          placeholder="Ticker Symbol"
          value={this.state.symbol}
          onChange={e => this.change(e)}
        />
        <br />
        <input
          name="quantity"
          type="number"
          min="0"
          placeholder="Quantity"
          value={this.state.quantity}
          onChange={e => this.change(e)}
        />
        <br />
        <button onClick={e => this.onSubmit(e)}>Submit</button>
      </form>
    );
  }
}
