import React from "react";
import Button from "@material-ui/core/Button";
import { Field, reduxForm } from "redux-form";
import Popup from "reactjs-popup";
import { Table, Container, Input, Label, Form, FormGroup } from "reactstrap";
import DatePicker from "react-datepicker";
import { Link } from "react-router-dom";
import "react-datepicker/dist/react-datepicker.css";
class StockPopup extends React.Component {
  emptyItem = {
    description: "",
    expensedate: new Date(),
    id: "",
    location: "",
    category: { id: 1, name: "Travel" },
    price: "",
  };
  constructor(props) {
    super(props);

    this.state = {
      isLoading: false,
      categories: [],
      Expenses: [],
      date: new Date(),
      item: this.emptyItem,
      price: [],
    };
  }
  render() {
    return (
      <Popup
        trigger={<button> Add New Expense</button>}
        position="right center"
      >
        <Form onSubmit={this.handleSubmit}>
          <FormGroup>
            <Label for="description">Description of Expense</Label>
            <Input
              type="description"
              name="description"
              id="description"
              autoComplete="name"
            />
            <Label for="location">Location</Label>
            <Input type="text" name="location" id="location" />
            <Label for="price">Price</Label>
            <Input type="number" name="price" id="price" />
            <Label for="category">Category</Label>
            <select>{}</select>
            <div>
              <Label for="city">Date</Label>
              <DatePicker selected={this.state.item.expensedate} />
            </div>
          </FormGroup>

          <FormGroup>
            <Button color="primary" type="submit">
              Save
            </Button>
            <Button color="secondary" tag={Link} to="/">
              Cancel
            </Button>
          </FormGroup>
        </Form>
      </Popup>
    );
  }
}

export default StockPopup;
