import React, { Component } from "react";
import Form from "./StockForm";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

function createData(symbol, datePurchased, purchasePrice, qty, ) {
  return { symbol, datePurchased, purchasePrice, qty };
}

class StockPopup extends Component {
  state = {
    fields: {},
    buyDate: new Date(),
    buyDateString:"",
    sellDate: new Date(),
  };

  onChange = updatedValue => {

    this.setState({
      fields: {
        ...this.state.fields,
        ...updatedValue
      }
    });
    //make api call 
  };
  // dateParse = (date) => {
  //   var onlyDate = date.toString().substr(0,date.toString().indexOf(','));
  //   console.log(onlyDate);
  //   return onlyDate
  // }
  handleChange = date => {  
    var stringDate = `${date.getFullYear().toString()}-${(date.getMonth()+1).toString()}-${date.getDate().toString()}`;
    this.setState({
      buyDate: date,
      buyDateString:stringDate
    });
  };

  render() {
    return (
      <div className="App">
        <DatePicker selected={this.state.buyDate} onChange={this.handleChange}/>
        <Form onChange={fields => this.onChange(fields)} />]\
        <p>{JSON.stringify(this.state, null, 3)}</p>

      </div>
    );
  }
}


export default StockPopup;

