import React from "react";
import Button from "@material-ui/core/Button";
import { Field, reduxForm } from "redux-form";
import Popup from "reactjs-popup";
import { Table, Container, Input, Label, Form, FormGroup } from "reactstrap";
import DatePicker from "react-datepicker";
import { Link } from "react-router-dom";
import "react-datepicker/dist/react-datepicker.css";
import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles((theme) => ({
  center: {
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
    textAlign: "center",
  },
}));
class StockPopup extends React.Component {
  emptyItem = {
    description: "",
    expensedate: new Date(),
    id: "",
    location: "",
    category: { id: 1, name: "Travel" },
    price: "",
  };
  constructor(props) {
    super(props);

    this.state = {
      isLoading: false,
      categories: [],
      Expenses: [],
      date: new Date(),
      item: this.emptyItem,
      price: [],
    };
  }
  render() {
    return (

          <div className="center">
        <Popup trigger={<button> Add New Expense</button>} position="right center">
        <Form onSubmit = {this.handleSubmit}>
            <FormGroup>
                <Label for="description">Description of Expense</Label>
                <Input type = "description" name = "description" id = "description"
                 onChange={this.handleChange} autoComplete = "name"/>
                <Label for ="location">Location</Label>
                <Input type="text" name="location" id="location" onChange={this.handleChange}/>
                <Label for="price">Price</Label>
                <Input type="number" name="price" id="price" onChange={this.handleChange}/>
                <Label for="category">Category</Label>
                <select onChange={this.handleCatChange}>
                        {optionList}
                </select>
                <div>
                <Label for="city">Date</Label>
                <DatePicker selected={this.state.item.expensedate}  onChange={this.handleDateChange} />

                </div>
                   </FormGroup>

            <FormGroup>
                <Button color = "primary" type = "submit">Save</Button>
                <Button color = "secondary" tag = {Link} to ="/">Cancel</Button>
            </FormGroup>
        </Form>
        </Popup>
        </div>
    );
  }
}

export default StockPopup;
