import { GET_STOCK_INFO } from "../actions/types";

const INTIAL_STATE = {
  companyInfo: {},
  currentCompany: "AMZN",
};
export default (state = INTIAL_STATE, action) => {
  switch (action.type) {
    case GET_STOCK_INFO:
      return { ...state, companyInfo: action.payload };
    case CHANGE_COMPANY:
        return {...state,currentCompany:action.payload};
    default:
      return state;
  }
};
