import React from 'react';
import {connect} from 'react-redux';
import {signIn,signOut,createPerson} from '../actions';

class GoogleAuth extends React.Component {

  componentDidMount() {
    window.gapi.load('client:auth2', () => {
      window.gapi.client
        .init({
          clientId:
          '895329232490-cj7fe970fah76sntch6gn2i7n1st0ljd.apps.googleusercontent.com',  
          scope: 'email',
        })
        .then(() => {
          this.auth = window.gapi.auth2.getAuthInstance();
          this.onAuthChange(this.auth.isSignedIn.get())
          this.auth.isSignedIn.listen(this.onAuthChange);
        });
    });
  }

  onAuthChange = (isSignedIn) => {
    if(isSignedIn){
        this.props.signIn(this.auth.currentUser.get().getId());
        this.props.createPerson(this.auth.currentUser.get().getId());
    }else{
        this.props.signOut();
    }
  };

  onSignInClick = () => {
    this.auth.signIn(); //mgl
    //add new person id if one doesnt exists 
  };

  onSignOutClick = () => {
    this.auth.signOut();
  };

  renderAuthButton() {
    if (this.props.isSignedIn === null) {
      return null;
    } else if (this.props.isSignedIn) {
      return (
        <button onClick={this.onSignOutClick} className="ui red google button">
          <i className="google icon" />  
          Sign Out
        </button>
      );
    } else {
      return (
        <button onClick={this.onSignInClick} className="ui red google button">
          <i className="google icon" />
          Sign In with Google
        </button>
      );
    }
  }

  render() {
    return <div>{this.renderAuthButton()}</div>;
  }
}
const mapStateToProps = (state) => {
    return {isSignedIn:state.auth.isSignedIn};
};

export default connect(mapStateToProps, {signIn,signOut,createPerson}) (GoogleAuth);
