import React from "react";
import {ResponsiveContainer} from "recharts";
import Title from "./Title";
import Plot from 'react-plotly.js';
import { connect } from 'react-redux';
import { getStocks } from '../actions';


class Chart extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      stockChartXValues: [],
      stockChartYValues: []
    }
  }

  componentDidMount() {
    this.fetchStock();
    this.props.getStocks(this.props.currentCompany);
  }

  fetchStock() {
    const pointerToThis = this;
    console.log(pointerToThis);
    const API_KEY = '82N2UO66AVHA2EFY';
    let StockSymbol = pointerToThis.props.companies;
    let API_Call = `https://www.alphavantage.co/query?function=TIME_SERIES_DAILY_ADJUSTED&symbol=${StockSymbol}&outputsize=compact&apikey=${API_KEY}`;
    let stockChartXValuesFunction = [];
    let stockChartYValuesFunction = [];

    fetch(API_Call)
      .then(
        function(response) {
          return response.json();
        }
      )
      .then(
        function(data) {
          //console.log(data);

          for (var key in data['Time Series (Daily)']) {
            stockChartXValuesFunction.push(key);
            stockChartYValuesFunction.push(data['Time Series (Daily)'][key]['1. open']);
          }

          // console.log(stockChartXValuesFunction);
          pointerToThis.setState({
            stockChartXValues: stockChartXValuesFunction,
            stockChartYValues: stockChartYValuesFunction
          });
        }
      )
  }
  render() {
    return  (
      <React.Fragment>
      <Title>Today</Title>
      <ResponsiveContainer>
      <Plot
          data={[
            {
              x: this.state.stockChartXValues,
              y: this.state.stockChartYValues,
              type: 'scatter',
              mode: 'lines+markers',
              marker: {color: 'red'},
            }
          ]}
          layout={{width: 850, height: 440, title: this.props.currentCompany}}
        />
      </ResponsiveContainer>
    </React.Fragment>
    );
  };
}
const mapStateToProps = (state) => {
  return {
    companies:state.stocks.currentCompany
  };
};
export default connect(
  mapStateToProps,
  { getStocks }
)(Chart);
