import React from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

function createData(symbol, datePurchased, purchasePrice, qty, ) {
  return { symbol, datePurchased, purchasePrice, qty };
}
 class StockForm extends React.Component {
  state = {
    symbol: "",
    quantity: "",
    buyDateString:"2020-09-17",
    price:0,
  };

  change = e => {
    //recieves on change as props 
    this.props.onChange({ [e.target.name]: e.target.value });
    this.setState({
      [e.target.name]: e.target.value
    });
    console.log(e.target.name);
  };
// resets data but i want additional functionality 
// here i want to take state data and put it into object
//pass that object into action creator and dispatch it to database
//then i want to pull the data from database and dispaly it on Orders
//refer to action creator of steram create it creates and objects and posts it to the api 

  onSubmit = e => {
    const API_KEY = 'HGJWFG4N8AQ66ICD';
    let StockSymbol = this.state.symbol.toUpperCase();
    console.log(StockSymbol);
    console.log("KLSDJF:LSDKFJ:SLDKJK")
    let API_Call = `https://www.alphavantage.co/query?function=TIME_SERIES_DAILY_ADJUSTED&symbol=${StockSymbol}&outputsize=compact&apikey=${API_KEY}`;
    fetch(API_Call)
      .then(
        function(response) {
          return response.json();
        }
      )
      .then(
        function(data) {
          console.log(data);

          if ('Note' in data){
             alert(data.Note);
          }
          else if ('Error Message' in data){
            alert("No Ticker exists for your input String Please input valid Ticker Symbol");
          }else{
             var printout = data['Time Series (Daily)'][this.state.buyDateString]['4. close'];
             console.log(printout);
          }

        });
//very important to remember this will be what i pass into reducer

    //******************** */
    e.preventDefault();
    this.setState({
      symbol: "",
      quantity: "",

    });
    this.props.onChange({
      symbol: "",
      quantity: "",
    });
  };
  handleChange = date => {  
    var tempMonth = "";
    if ((date.getMonth()+1) < 10){
      tempMonth = "0"+(date.getMonth()+1).toString();
    }else{
      tempMonth = (date.getMonth()+1).toString()
    }
    var stringDate = `${date.getFullYear().toString()}-${tempMonth}-${date.getDate().toString()}`;
    console.log(tempMonth);
    this.setState({
      buyDate: date,
      buyDateString:stringDate
    });
  };

  render() {
    return (
      <form>
        <label>What day did you buy stock?</label>
        <DatePicker selected={this.state.buyDate} onChange={this.handleChange}/>
        <input
          name="symbol"
          placeholder="Ticker Symbol"
          value={this.state.symbol}
          onChange={e => this.change(e)}
        />
        <br />
        <input
          name="quantity"
          type="number"
          min="0"
          placeholder="Quantity"
          value={this.state.quantity}
          onChange={e => this.change(e)}
        />
        <br />
        <button onClick={e => this.onSubmit(e)}>Submit</button>
      </form>
    );
  }
}
export default StockForm; //no current company
